import React from "react";

import { Divider, Container } from "semantic-ui-react";

import QueryPage from "./QueryPage";

// Change to add new components to the header
// The first item will be the opening page after login
const items = [
  { label: "Home", path: "/home", Component: "" },
  {
    label: "Manage Indices",
    path: "/indices",
    position: "left",
    Component: "",
  },
  { label: "Run Queries", path: "/query", Component: "" },
  // { label: 'Browse Index', path: '/indexDetail', Component: IndexDetail },
  {
    label: "Browse Document",
    path: "/browseDocument",
    Component: "",
  },
  {
    label: "Manage Users and Access",
    path: "/userManagement",
    position: "right",
    Component: "",
  },
];

const App = () => {
  const createNavigation = (items) => {
    return items.map((item) => {
      return (
        <AuthRoute
          key={item.path}
          path={item.path}
          Component={item.Component}
        />
      );
    });
  };

  return (
    <Router history={history}>
      {/* rendering the headerMenu items */}
      <HeaderMenu items={items} />
      <Divider />
      <Container style={{ marginTop: "4em" }}>
        <Switch>
          <Route exact path="/" render={() => <AmcatLogin items={items} />} />
          {createNavigation(items)}
          <QueryPage />
        </Switch>
      </Container>
    </Router>
  );
};

export default App;
