import React from "react";

import _ from "lodash";

import { connect } from "react-redux";
import { setDocuments, setQueryString } from "../actions";

import { Button, Card, Label } from "semantic-ui-react";

class Records extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      queryMethod: "POST",
      accordionActive: false,
    };
    this.fields = Object.keys(this.props.fields);
  }

  createCards() {
    return this.props.documents.map((document) => {
      return (
        <Card
          name={document.title}
          date={document.date}
          doi={document.doi}
          language={document.language}
          measurement={document.measurement}
          method={document.method}
          method2={document.method2}
          validation={document.validation}
          text={document.text}
          url={document.url}
        />
      );
    });
  }

  createACard(props) {
    console.log(props);
    return (
      <Card>
        <Card.Content>
          <i class="file alternate outline icon large right floated" />
          <Card.Header>{props.name}</Card.Header>
          <Card.Meta>{props.text}</Card.Meta>
          <Card.Description>
            Steve wants to add you to the group <strong>best friends</strong>
          </Card.Description>
        </Card.Content>

        <Card.Content extra>
          {this.createLabels()}
          <br />
          <div className="ui two buttons">
            <Button basic color="green">
              Approve
            </Button>
            <Button basic color="red">
              Decline
            </Button>
          </div>
        </Card.Content>
      </Card>
    );
  }

  createLabels() {
    return (
      <div>
        <Label as="a" tag>
          New
        </Label>
        <Label as="a" color="red" tag>
          Upcoming
        </Label>
        <Label as="a" color="teal" tag>
          Featured
        </Label>
      </div>
    );
  }

  render() {
    return (
      <Card.Group stackable itemsPerRow={3}>
        {this.createCards()}
      </Card.Group>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    session: state.session,
    index: state.index,
    fields: state.indexFields,
    filters: state.fieldValues,
    queryString: state.queryString,
    documents: state.documents,
  };
};

export default connect(mapStateToProps, { setDocuments, setQueryString })(
  Records
);
