import React from "react";

import { connect } from "react-redux";
import { setDocuments, setQueryString } from "../actions";

import { Button, Card, Label, Divider } from "semantic-ui-react";

class Records extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      queryMethod: "POST",
      accordionActive: false,
    };
    this.fields = Object.keys(this.props.fields);
  }

  createCards() {
    return this.props.documents.map((document) => {
      return this.createACard(document);
      // <Card
      //   name={document.title}
      //   date={document.date}
      //   doi={document.doi}
      //   language={document.language}
      //   measurement={document.measurement}
      //   method={document.method}
      //   method2={document.method2}
      //   validation={document.validation}
      //   text={document.text}
      //   url={document.url}
      // />
    });
  }

  createACard(document) {
    semaireturn(
      <Card>
        <Card.Content>
          <i class="file alternate outline icon large right floated" />
          <Card.Header>{document.title}</Card.Header>
          <Card.Meta>Date: {document.date}</Card.Meta>
          <Card.Meta>DOI: {document.doi}</Card.Meta>
          <Card.Description>Note about the record: ...</Card.Description>
        </Card.Content>

        <Card.Content extra>
          {this.createLabels(document)}
          <br />
          <div className="ui two buttons">
            <Button basic color="green">
              Edit
            </Button>
            <Button basic color="red">
              Delete
            </Button>
          </div>
        </Card.Content>
      </Card>
    );
  }

  createLabels(document) {
    return (
      <div>
        <Label as="a" color="green" tag style={{ marginBottom: "0.5em" }}>
          Language: {document.language}
        </Label>
        <br />
        <Label as="a" color="red" tag style={{ marginBottom: "0.5em" }}>
          Measurement: {document.measurement}
        </Label>
        <br />
        <Label as="a" color="teal" tag style={{ marginBottom: "0.5em" }}>
          Method: {document.method}
        </Label>
        <br />
        <Label as="a" color="teal" tag style={{ marginBottom: "0.5em" }}>
          Secondary Method: {document.method2}
        </Label>
        <br />
        <Label as="a" color="orange" tag style={{ marginBottom: "0.5em" }}>
          Validation: {document.validation}
        </Label>
        <br />
      </div>
    );
  }

  render() {
    return (
      <Card.Group stackable itemsPerRow={3}>
        {this.createCards()}
      </Card.Group>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    session: state.session,
    index: state.index,
    fields: state.indexFields,
    filters: state.fieldValues,
    queryString: state.queryString,
    documents: state.documents,
  };
};

export default connect(mapStateToProps, { setDocuments, setQueryString })(
  Records
);
