import React from "react";

import { useDispatch, useSelector } from "react-redux";
import _ from "lodash";

import { Button, Card, Label } from "semantic-ui-react";

const Records = ({ runQuery }) => {
  const documents = useSelector((state) => state.documents);

  const dispatch = useDispatch();

  const createCards = () => {
    return documents.map((document) => {
      return createACard(document);
    });
  };

  const createACard = (document) => {
    return (
      <Card>
        <Card.Content>
          <i classname="file alternate outline icon large right floated" />
          <Card.Header>{document.title}</Card.Header>
          <Card.Meta>Date: {document.date}</Card.Meta>
          <Card.Meta>DOI: {document.doi}</Card.Meta>
          <Card.Description>Note about the record: ...</Card.Description>
        </Card.Content>

        <Card.Content extra>
          {createLabels(document)}
          <br />
          <div className="ui two buttons">
            <Button basic color="blue">
              Edit
            </Button>
            <Button basic color="red">
              Delete
            </Button>
          </div>
        </Card.Content>
      </Card>
    );
  };

  const createLabels = (document) => {
    return (
      <div>
        <Label
          as="a"
          color="green"
          tag
          style={{ marginBottom: "0.5em" }}
          onClick={() => {
            this.props.setQueryString(document.language);
          }}
        >
          Language: {document.language}
        </Label>
        <br />
        <Label
          as="a"
          color="red"
          tag
          style={{ marginBottom: "0.5em" }}
          onClick={() => this.props.setQueryString(document.measurement)}
        >
          Measurement: {document.measurement}
        </Label>
        <br />
        <Label
          as="a"
          color="teal"
          tag
          style={{ marginBottom: "0.5em" }}
          onClick={() => this.props.setQueryString(document.method)}
        >
          Primary Method: {document.method}
        </Label>
        <br />
        <Label
          as="a"
          color="teal"
          tag
          style={{ marginBottom: "0.5em" }}
          onClick={() => this.props.setQueryString(document.method2)}
        >
          Secondary Method: {document.method2}
        </Label>
        <br />
        <Label
          as="a"
          color="olive"
          tag
          style={{ marginBottom: "0.5em" }}
          onClick={() => this.props.setQueryString(document.validation)}
        >
          {document.validation}
        </Label>
        <br />
      </div>
    );
  };

  return (
    <Card.Group stackable itemsPerRow={3}>
      {createCards()}
    </Card.Group>
  );
};

export default Records;
